Spawn:
// float rand; Every read returns a random float [0 .. 1)
layout(ctype=float) in uniform float dt;
layout(ctype=float) in uniform float effectAge;

struct Particle {
  float  age;
  float  lifetime;
  float2 pos;
  float2 dir;
  float  scale;
  float2 vel;
  float  spin;
  float4 color;
  float  frame;
};

void main(inout Particle p) {
  p.lifetime = 10;
  p.vel.y = 10 + rand * 20;
  p.vel.x = -5 + 10 * rand;
  p.pos.x = rand * 500;
}

Update:
// float rand; Every read returns a random float [0 .. 1)
layout(ctype=float) in uniform float dt;
layout(ctype=float) in uniform float effectAge;

struct Particle {
  float  age;
  float  lifetime;
  float2 pos;
  float2 dir;
  float  scale;
  float2 vel;
  float  spin;
  float4 color;
  float  frame;
};

void main(inout Particle p) {
  p.scale = size(p.age);
}
