Spawn:
// float rand; Every read returns a random float [0 .. 1)
layout(ctype=float) in uniform float dt;
layout(ctype=float) in uniform float effectAge;

struct Particle {
  float  age;
  float  lifetime;
  float2 pos;
  float2 dir;
  float  scale;
  float2 vel;
  float  spin;
  float4 color;
  float  frame;
};

void main(inout Particle p) {
  p.lifetime = 1 + 2 * rand;
  p.pos.x = rand * 50;
  float a = radians(-10 + 20 * rand);
  float s = 50 + 10 * rand;
  p.vel.x = sin(a) * s;
  p.vel.y = -cos(a) * s;
}

Update:
// float rand; Every read returns a random float [0 .. 1)
layout(ctype=float) in uniform float dt;
layout(ctype=float) in uniform float effectAge;

struct Particle {
  float  age;
  float  lifetime;
  float2 pos;
  float2 dir;
  float  scale;
  float2 vel;
  float  spin;
  float4 color;
  float  frame;
};

void main(inout Particle p) {
  p.vel.x += wind(p.age) * dt;
  p.scale = 3 - (1.5 * p.age);
  p.color = color(p.age);
}
